// n8n Code node (JavaScript). Mode: "Run Once for Each Item"
function titleCaseName(s) {
  if (!s || typeof s !== 'string') return s;
  return s
    .trim()
    .split(/\s+/)
    .map(w => w[0] ? (w[0].toUpperCase() + w.slice(1).toLowerCase()) : w)
    .join(' ');
}

function clean(v) {
  if (v === undefined || v === null) return null;
  const s = String(v).trim();
  return s === '' ? null : s;
}

const out = [];

for (const item of items) {
  // 1) Get raw model output (handles various OpenAI node shapes)
  let raw =
    item.json?.text ??                           // common "Simplify Output" field
    item.json?.content ??                        // some OpenAI nodes
    item.json?.response ??                       // alt
    item.json?.choices?.[0]?.message?.content ?? // raw OpenAI
    item.json;                                   // last resort

  // 2) Parse to object (strip code fences, smart quotes, double-parse fallback)
  let obj = raw;
  if (typeof raw === 'string') {
    let s = raw.trim().replace(/^```(?:json)?\s*|\s*```$/g, '');
    s = s.replace(/[\u201C\u201D]/g, '"').replace(/[\u2018\u2019]/g, "'");
    try { obj = JSON.parse(s); }
    catch {
      obj = JSON.parse(JSON.parse(s)); // handles quoted-JSON-in-a-string
    }
  }

  // 3) Accept common alias keys from the model, then normalize
  const aliases = {
    name: ['name', 'full_name', 'person', 'candidate'],
    job_title: ['job_title', 'title', 'role', 'position', 'headline'],
    company: ['company', 'org', 'employer', 'organization'],
    location: ['location', 'city', 'based_in', 'place'],
    university: ['university', 'school', 'alma_mater', 'education'],
    description: ['description', 'summary', 'about', 'bio']
  };

  const pick = (o, keys) => {
    for (const k of keys) if (o && o[k] != null) return o[k];
    return null;
  };

  const result = {
    name:        titleCaseName(clean(pick(obj, aliases.name))),
    job_title:   clean(pick(obj, aliases.job_title)),
    company:     clean(pick(obj, aliases.company)),
    location:    clean(pick(obj, aliases.location)),
    university:  clean(pick(obj, aliases.university)),
    description: clean(pick(obj, aliases.description))
  };

  // 4) Optional: trim description length
  if (result.description && result.description.length > 300) {
    result.description = result.description.slice(0, 297) + '...';
  }

  out.push({ json: result });
}

return out;

